```php
function extract_text($html, $length) {
        // 剥离HTML标签
        $text = strip_tags($html);
        // 提取前100个字符
        $text = mb_substr($text, 0, $length, 'UTF-8');

        // 如果截取的位置刚好在HTML标签内，则去除截断的不完整标签
        $lastTagEndPos = strrpos($text, '>');
        $lastTagStartPos = strrpos($text, '<');
        if ($lastTagStartPos !== false && $lastTagEndPos === false) {
            $text = mb_substr($text, 0, $lastTagStartPos, 'UTF-8');
        }

        // 如果截取的位置在HTML实体编码内，则将实体编码转换回原始字符
        $text = html_entity_decode($text, ENT_QUOTES | ENT_HTML5, 'UTF-8');

        return $text;
    }

