                        let re= /[\u4e00-\u9fa5]/g;
                        let vHtml = $(v).contents().filter(function (index, content) {return content.nodeType === 3}).text().trim();
                        // 跳过script标签
                        if (re.test(vHtml) && v.tagName != 'SCRIPT') {
                            // 防止标签里面还有标签，所以只替换里面的html,使用replace
                            $(v).html(
